(function(doc){
	
	var emailElems = doc.getElementsByClassName( 'email' )
	,	len = emailElems.length
	,	elem
	,	email = 'mi' + 'ke@' + 'mikevanross' + 'um.nl'
	;
	while( len-- ) {
		elem = emailElems[len];
		
		if(elem.tagName === 'A') {
			elem.setAttribute( 'hre' + 'f', 'mai' + 'lto' + ':' + email );
			if(elem.innerHTML === 'email') {
				elem.innerHTML = email;
			}
		}
	}
	
}(document));